function createSatHere(row, col){
    const values = Object.values(sat_list);
    for (const value of values) {
        if (row == value[0] && col == value[1]){
            return false;
        }     
    }
    return true;
}

let grid_size = 300;

var sat1_x = runtimeScene.getVariables().get("sat1_x_position").getAsNumber();
var sat1_y = runtimeScene.getVariables().get("sat1_y_position").getAsNumber();
var sat2_x = runtimeScene.getVariables().get("sat2_x_position").getAsNumber();
var sat2_y = runtimeScene.getVariables().get("sat2_y_position").getAsNumber();

var random_x = runtimeScene.getVariables().get("sat_random_position_x").getAsNumber();
var random_y = runtimeScene.getVariables().get("sat_random_position_y").getAsNumber();

// Create left or right satellite
var sat_orientation = runtimeScene.getVariables().get("third_sat_orientation").getAsNumber();
let right_or_left_sat = sat_orientation < 50 ? "sat_right_2" : "sat_left_2";
var created_sat = runtimeScene.createObject(right_or_left_sat);
created_sat.setPosition(random_x, random_y);
created_sat.setScale(runtimeScene.getVariables().get("satellite_scale").getAsNumber());

// initialize grid to all empty
var points_used = Array.from(Array(grid_size), () => new Array(grid_size));
for (let row = 50; row < grid_size; row+=100){
    for (let col = 50; col < grid_size; col+=100){
        points_used[row][col] = false;
    }
}
// location of already placed satellites
points_used[sat1_x][sat1_y] = true;
points_used[sat2_x][sat2_y] = true;
let grid = "";
for (let row = 50; row < grid_size; row+=100){
    for (let col = 50; col < grid_size; col+=100){
        grid += points_used[col][row] + "\t";
        if ((points_used[row][col] == true) && (row == random_x) && (col == random_y)){
            created_sat.deleteFromScene(runtimeScene);
            gdjs.RuntimeObject.setVariableString(runtimeScene.getObjects("debug_4")[0], "(" + random_x + ", " + random_y + ")");
            return;
        }
    }
    grid += "\n"
}

gdjs.RuntimeObject.setVariableString(runtimeScene.getObjects("debug_1")[0], "(" + sat1_x + ", " + sat1_y + ")");
gdjs.RuntimeObject.setVariableString(runtimeScene.getObjects("debug_2")[0], "(" + sat2_x + ", " + sat2_y + ")");
gdjs.RuntimeObject.setVariableString(runtimeScene.getObjects("debug_3")[0], "(" + random_x + ", " + random_y + ")");
gdjs.RuntimeObject.setVariableString(runtimeScene.getObjects("debug_5")[0], grid);


runtimeScene.getVariables().get("sats_created").setNumber(runtimeScene.getVariables().get("sats_created").getAsNumber() + 1);
